                f="САУ ГПА",
                }
local oldsignal = {} -- буферная таблица  предыдущего состояния входов
local Log=false -- ведение логов сообщений
local event ={  --таблица типов событий
                a=10000 , --АС
                w=10100, --ПС
                o=101,  --оператор
                t=102,  --ТС
                p=9998,  --регулятор
}

local function Add_Event (Tag, signal, text, class, source_f)-- функция добавления события по сигналу Tag со значением signal в журнал
        DT=os.time() -- время события (локальное)
        local e_type -- тип события (появление\исчезновение)
        local user=Core.USER_NAME_NOW[0] -- текущее имя  пользователя
        if signal then  -- определение типа события (появление\ исчезновение)
                e_type=1
                if Log then Core.addLogMsg(os.date().." ".."(Появл.) " .. text) end --добавление события в лог 
        else
                e_type=0
                if Log then Core.addLogMsg(os.date().." ".."(Исчезн.) " .. text) end --добавление события в лог
        end
        if oldsignal[Tag]==nil or oldsignal[Tag]~=signal then --если сигнал изменился
                Core.addEvent(text,
                                          class,
                                          e_type,
                                          source_f,
                                          user,
                                          Tag,
                                          DT,
                                          "") --  добавляем событие (типа e_type), текст берем в таблице msg, класс сообщения в таблице event_class
        end -- если сигнал изменился
end -- of Add_Event

